var loadFile = function(event) { 
    var cont = document.querySelector('.image-cont'); 
    let spanLabel = document.createElement('span');
    spanLabel.innerHTML = "Добавить еще фото"
    cont.innerHTML = '';
    
    for (let i = 0; i < event.target.files.length; i++) { 
        let imgCreate = document.createElement('img');   
        
        imgCreate.src =  URL.createObjectURL(event.target.files[i]); 
        cont.appendChild(imgCreate)  
    }  
    cont.appendChild(spanLabel)
}  
let openMenu = document.querySelector('.js-open-menu');
let closeMenu = document.querySelector('.js-close-menu');
let menu = document.querySelector('.js-menu-collapse');

openMenu.addEventListener('click', function() { 
    menu.classList.add('open'); 
    menu.classList.remove('close');
});
closeMenu.addEventListener('click', function() { 
    toCloseMenu();
});

function  toCloseMenu() { 
    menu.classList.remove('open');
    menu.classList.add('close');
}

window.addEventListener('click', function(e) {  
    if(menu.classList.contains('open')) {  
        if( !menu.contains(e.target) && e.target != menu && e.target != openMenu) { 
            toCloseMenu() 
        }
    }
})
