let btnTab = document.querySelectorAll('.js-tab-head a'); 
let btnTabLi = document.querySelectorAll('.js-tab-head li'); 
let contentTab = document.querySelectorAll('.js-tab-content > div')

btnTab.forEach( (item, i) => { 
    btnTab[i].addEventListener('click', function(e) { 
        e.preventDefault(); 
        let bntAtr = e.target.getAttribute('data-tab-btn')
         
        btnTab[i].classList.add('active')
        btnTabLi[i].classList.add('active')
        for(let n = 0 ; n < btnTab.length; n++ ) { 
            if( n == i) continue;
            btnTab[n].classList.remove('active') ;
            btnTabLi[n].classList.remove('active') ;
        }

        showContentTab(bntAtr)

    })
})

function showContentTab(att) { 
    contentTab.forEach( (item,i) => { 
        if(contentTab[i].getAttribute('data-tab-content') == att) { 
            contentTab[i].classList.add('active'); 
            
            for(let n = 0 ; n < contentTab.length; n++ ) { 
                if( n == i) continue;
                contentTab[n].classList.remove('active') ;
            }
        }
    })
}
 